CREATE FUNCTION public.alkis_mviews() RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
	sql TEXT;
	delim TEXT;
	c RECORD;
BEGIN
	SELECT alkis_dropobject('vbeziehungen') INTO sql;
	SELECT alkis_dropobject('vobjekte') INTO sql;

	delim := '';
	sql := 'CREATE VIEW vobjekte AS ';

	FOR c IN SELECT table_name FROM information_schema.columns
		   WHERE column_name='gml_id'
		     AND substr(table_name,1,3) IN ('ax_','ap_','ks_','aa_')
		     AND NOT table_name IN ('ax_tatsaechlichenutzung','ax_klassifizierung','ax_ausfuehrendestellen') LOOP
		sql := sql || delim || 'SELECT gml_id,beginnt,endet,''' || c.table_name || ''' AS table_name FROM ' || c.table_name;
		delim := ' UNION ';
	END LOOP;

	EXECUTE sql;

	CREATE VIEW vbeziehungen AS
		SELECT beziehung_von,(SELECT DISTINCT table_name FROM vobjekte WHERE gml_id=beziehung_von) AS typ_von
			,beziehungsart
			,beziehung_zu,(SELECT DISTINCT table_name FROM vobjekte WHERE gml_id=beziehung_zu) AS typ_zu
		FROM alkis_beziehungen;

	RETURN 'ALKIS-Views erzeugt.';
END;
$$;


--
-- Name: alkis_offsetcurve(public.geometry, double precision, text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.alkis_offsetcurve(g0 public.geometry, offs double precision, params text) RETURNS public.geometry
    LANGUAGE plpgsql IMMUTABLE
    AS $$
DECLARE
        i INTEGER;
	n INTEGER;
	p0 GEOMETRY;
	p1 GEOMETRY;
	p2 GEOMETRY;
	p00 GEOMETRY;
	p01 GEOMETRY;
	p10 GEOMETRY;
	p11 GEOMETRY;
	d0 float8;
	d1 float8;
	dx float8;
	dy float8;
	r GEOMETRY[];
	g GEOMETRY;
BEGIN
	IF params IS NULL OR params<>'' THEN
		RAISE EXCEPTION 'alkis_offsetcurve: params % nicht unterstützt.', params;
	END IF;

	IF geometrytype(g0)='MULTILINESTRING' THEN
		IF st_numgeometries(g0)<>1 THEN
			RETURN NULL;
		END IF;
		g := st_geometryn(g0,1);
	ELSIF geometrytype(g0)<>'LINESTRING' THEN
		RETURN NULL;
	ELSE
		g := g0;
	END IF;

	n := st_numpoints(g);
	IF n IS NULL OR n<2 THEN
		RAISE EXCEPTION '% too short', st_astext(g);
		RETURN NULL;
	END IF;

	BEGIN
		p2 := st_pointn(g,1);
	EXCEPTION WHEN OTHERS THEN
		RAISE NOTICE 'could not get first point from: %', st_astext(g);
	END;

	FOR i IN 2..n LOOP
		p0 := p1;
		p1 := p2;
		d0 := d1;

		IF i>2 THEN
			IF d0=0 THEN
				RAISE EXCEPTION 'st_offsetcurve: doppelter Punkt';
			END IF;
			dx := (st_y(p0)-st_y(p1)) * offs / d0;
			dy := (st_x(p1)-st_x(p0)) * offs / d0;
			p00 := st_translate( p0, dx, dy );
			p01 := st_translate( p1, dx, dy );
		END IF;

		BEGIN
			p2 := st_pointn(g,i);
		EXCEPTION WHEN OTHERS THEN
			RAISE EXCEPTION 'could not get point % from: %', i, st_astext(g);
		END;

		d1 := st_distance( p1, p2 );
		IF d1=0 THEN
			p2 := p1;
			p1 := p0;
			d1 := d0;
			CONTINUE;
		END IF;

		dx := (st_y(p1)-st_y(p2)) * offs / d1;
		dy := (st_x(p2)-st_x(p1)) * offs / d1;

		p10 := st_translate( p1, dx, dy );
		p11 := st_translate( p2, dx, dy );

		IF i=2 THEN
			r := ARRAY[ p10 ];
		ELSE
			r := array_append( r, coalesce( alkis_intersect_lines( p00, p01, p10, p11 ), p01 ) );
		END IF;

		IF i=n THEN
			r := array_append( r, p11 );
		END IF;
	END LOOP;

	IF offs<0 THEN
		RETURN st_reverse( st_makeline(r) );
	ELSE
		RETURN st_makeline(r);
	END IF;
END;
$$;


